/**
 * Convert radians to degrees
 * @param radian
 * @returns {number}
 */
export declare function deg(radian: number): number;
/**
 * Convert degrees to radians
 * @param degree
 * @returns {number}
 */
export declare function rad(degree: number): number;
/**
 * Unwrap a radian to its equivalent form between [-PI, PI]
 * @param rad
 */
export declare function unwrapRad(rad: number): number;
/**
 * Clamp a number between a minimum and maximum value
 * @param {number} num
 * @param {number} min
 * @param {number} max
 * @returns {number} Clamped number
 */
export declare function clamp(num: number, min: number, max: number): number;
/**
 * Round a number to a fixed number of decimals
 * @param {number} num Number to round
 * @param {number} numDecimals Number of decimals
 * @returns {number}
 */
export declare function roundToDec(num: number, numDecimals: number): number;
